import React, {useState, FormEvent} from 'react';
import { FiChevronRight} from 'react-icons/fi'
import api from '../../services/api';

import logoImg from '../../assets/logo.svg';
import { Title, Form, Repositories } from './styles';

const Dashboard: React.FC = () => {
  const [newRepo, setNewRepo] = useState('');
  const [repositories, setRepositories] = useState([]);

  // FUnção para adicionar repositorio
  async function handleAddRepository(event: FormEvent<HTMLFormElement>): Promise<void>{
    event.preventDefault(); // Primeira coisa do comportamento padrão
       //console.log(newRepo);

    const response = await api.get(`repos/${newRepo}`);

    //console.log(response.data);
    const repository = response.data;

    setRepositories([...repositories, repository]);



  }

    return (
    <>
    <img src={logoImg} alt="GitHub Explorer" />
    <Title>Explore repositórios no Github</Title>
    <Form onSubmit={handleAddRepository}>
      <input value={newRepo}
      onChange={(e) => setNewRepo(e.target.value)} //
      placeholder="Digite o nome do repositório"/>
      <button type="submit">Pesquisar</button>
    </Form>


    <Repositories>
      <a href="teste">
      <img src="https://avatars2.githubusercontent.com/u/46175572?s=460&u=4bab6705a0563133fd38c5b3560d02010af2abff&v=4"
      alt="Luiz Humberto"/>
      <div>
        <strong>rocketseat/unform</strong>
        <p>Easy peasy highly scalable ReactJS & React Native forms! </p>
      </div>
      <FiChevronRight size={20} />
      </a>

      <a href="teste">
      <img src="https://avatars2.githubusercontent.com/u/46175572?s=460&u=4bab6705a0563133fd38c5b3560d02010af2abff&v=4"
      alt="Luiz Humberto"/>
      <div>
        <strong>rocketseat/unform</strong>
        <p>Easy peasy highly scalable ReactJS & React Native forms! </p>
      </div>
      <FiChevronRight size={20} />
      </a>

      <a href="teste">
      <img src="https://avatars2.githubusercontent.com/u/46175572?s=460&u=4bab6705a0563133fd38c5b3560d02010af2abff&v=4"
      alt="Luiz Humberto"/>
      <div>
        <strong>rocketseat/unform</strong>
        <p>Easy peasy highly scalable ReactJS & React Native forms! </p>
      </div>
      <FiChevronRight size={20} />
      </a>
    </Repositories>
  </>
  )
};

export default Dashboard;
