import styled from 'styled-components';

// Template literals


export const Title = styled.h1`
  font-size: 48px;
  color: #3a3a3a;
  max-width: 450px;
  line-height: 56px;

  margin-top: 80px;
`;

export const Form = styled.form `
  margin-top: 40px;
  max-width:700px;
  display: flex; /* forçar lado do outro */

  input{
    flex:1; /* Vai ocupar tudo do 700, menos o tamanho do botao  */
    height: 70px;
    padding: 0 20px;
    border:0;
    border-radius: 5px 0 0 5px;

  }

  button{
    width: 210px;
    height: 70px;
    background: #04D361;
    border-radius: 0px 5px 5px 0px;
    border: 0;
    color: #fff;
    font-weight: bold;

  }
`

;
